    private void quiz_btn_startActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_quiz_btn_startActionPerformed
        listener.quizBtnStartToggled();
        
        if (quiz_tBtn_3alt.isSelected()) {
            changePanel(panel_quiz_3alt);
        } else if (quiz_tBtn_freetext.isSelected()){
            changePanel(panel_quiz_freetext);
        }
    }//GEN-LAST:event_quiz_btn_startActionPerformed

